<?php	//------ init GET//	if (!isset($_REQUEST["day"])) $_REQUEST["day"] = "";	$choose_day = $_GET["day"];//	$choose_day = 10;  $monthNames = Array("January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December");//	if (!isset($_REQUEST["month"])) $_REQUEST["month"] = date("n");//	if (!isset($_REQUEST["year"]))  $_REQUEST["year"]  = date("Y");	  $cMonth = $_REQUEST["month"];	if($cMonth == "") $cMonth = date("n");  $cYear  = $_REQUEST["year"];	if($cYear == "") $cYear = date("Y");                  $prev_year = $cYear;  $next_year = $cYear; 	$prev_month = $cMonth-1; 	$next_month = $cMonth+1; 	if ($prev_month == 0 ) {    $prev_month = 12;    $prev_year = $cYear - 1;  } 	if ($next_month == 13 ) {    $next_month = 1;    $next_year = $cYear + 1;  }?>   <div id="calendar_div" name="calendar_div">    <table width="180">        <tr align="center">          <td bgcolor="#ace9ff" style="color:#FFFFFF">            	<table width="100%" border="0" cellspacing="0" cellpadding="0">                    <tr>                      <td width="50%" align="left">&nbsp;&nbsp;<a href="<?php echo './index.php?fn='.$fn.'&month='.$prev_month.'&year='.$prev_year; ?>" style="color:#666666">Previous</a></td>                      <td width="50%" align="right"><a href="<?php echo './index.php?fn='.$fn.'&month='.$next_month.'&year='.$next_year; ?>" style="color:#666666">Next</a>&nbsp;&nbsp;</td>                    </tr>              </table>          </td>        </tr>        <tr>            <td align="center">              <table width="100%" border="0" cellpadding="0" cellspacing="0" style="table-layout: fixed">				<col width="14.285714286">				<col width="14.285714286">				<col width="14.285714286">				<col width="14.285714286">				<col width="14.285714286">				<col width="14.285714286">				<col width="14.285714286">                <tr align="center">                  <td colspan="7" bgcolor="#c4efff" style="color:#666666"><strong><?php echo $monthNames[$cMonth-1].' '.$cYear; ?></strong></td>                </tr>                <tr>                  <td align="center" bgcolor="ace9ff" style="color:#FF3333"><strong>S</strong></td>                  <td align="center" bgcolor="ace9ff" style="color:#FFFF33"><strong>M</strong></td>                  <td align="center" bgcolor="ace9ff" style="color:#FF88FF"><strong>T</strong></td>                  <td align="center" bgcolor="ace9ff" style="color:#33CC33"><strong>W</strong></td>                  <td align="center" bgcolor="ace9ff" style="color:#ff9019"><strong>T</strong></td>                  <td align="center" bgcolor="ace9ff" style="color:#19a0ff"><strong>F</strong></td>                  <td align="center" bgcolor="ace9ff" style="color:#d564ff"><strong>S</strong></td>                </tr>                <?php                 	$timestamp = mktime(0,0,0,$cMonth,1,$cYear);                	$maxday    = date("t",$timestamp);                	$thismonth = getdate ($timestamp);                	$startday  = $thismonth['wday'];                  for ($i=0; $i<($maxday+$startday); $i++) {                    if(($i % 7) == 0 ) echo "<tr>\n";                    if($i < $startday) //check the start day of the month					{	                    if(($i % 7) == 0 ) 							echo "<td bgcolor=\"#ffdada\" ></td>\n";						else							echo "<td></td>\n";					}                    else{	                    if(($i % 7) == 0 ) //check is today Sunday?							echo "<td align='center' bgcolor=\"#ffdada\" valign='middle' height='20px'>";						else							echo "<td align='center' valign='middle' height='20px'>";						echo "<div class=\"calendar_link\" ><a href=\"".$rootpath."main/my_calendars/index.php?fn=".$fn."&month=".$cMonth."&year=".$cYear."&day=".($i - $startday + 1)."\">";						if($choose_day == ($i - $startday + 1)) //check is it choose day?							echo "<font color = '#00a8ff'><b>".($i - $startday + 1) . "</b></font></td>\n";						elseif(date('d') == ($i - $startday + 1) &&  date('m') == $cMonth && date('Y') == $cYear) //check is it today?							echo "<font color = '#ffa800'><b>".($i - $startday + 1) . "</b></font></td>\n";						else							echo ($i - $startday + 1) . "</td>\n";						echo "</a></div>";					}                    if(($i % 7) == 6 ) echo "</tr>\n";                  }                 ?>              </table>            </td>        </tr>    </table></div>